<template>
 <div v-if="this.apts">
     <GmapMap ref="mapRef" :center="{lat : apts[0].lat *= 1 , lng : apts[0].lng*=1}" :zoom="13" style="width:400px; height:400px">
         <GmapMarker 
         :key="index" 
         v-for="(apt, index) in apts" 
         :position="{lat : apt.lat*=1 , lng : apt.lng*=1}"
         :title="'[' + apt.no + '] ' + apt.aptName"
         @click="toggleInfo(apt, index)"
         :clickable="true"
          />
        <GmapInfoWindow
          @closeclick="window_open=false"
          :opened="window_open"
         :position="infoWindowPos"><div v-html="infoContent"/></GmapInfoWindow>
     </GmapMap>
 </div>
</template>

<script>
import {mapState} from 'vuex';

export default {
    name: "Map",
    data(){
        return{
                window_open: false,
                infoContent: '',
                infoWindowPos:{
                    lat: 0,
                    lng: 0
                },
                currentMidx: null
        }
    },
    computed:{
        ...mapState(['apts'])
    },
    methods:{
        toggleInfo(marker, index){
            this.infoWindowPos.lat = marker.lat;
            this.infoWindowPos.lng = marker.lng;
            this.infoContent = "<img src='data:image/jpeg;base64,' style='height:100px;' /><br>" 
            + '[' +marker.no+'] ' + marker.aptName;
            if(this.currentMidx == index){
                this.window_open = !this.window_open;
            }else{
                this.window_open = true;
                this.currentMidx = index;
            }
        }
    }
}
</script>

